function Controler(context){
    var res = "";
    context.forEach(function(e, i) {
        switch(e.type){
            case "radio":
                if(e.attachment){
                    res += new radioAttach().render(e, i);     
                }           
                else{
                    res += new radio().render(e, i);   
                }             
            break;
        }        
    });
    return res;
}

function handleRadio(id ,e) {
    userResults.set(id, e.value);
}