'use strict';
function radioAttach(){}

radioAttach.prototype.label = function(id, e){
    return(
    '<p>'+
        '<label>'+
            '<input class="with-gap" name="group-' + id + '" type="radio" value="' + e.r + '" onclick="handleRadio(' + id + ', this);"/>'+
            '<span>' + e.r + '</span>'+
        '</label>'+
    '</p>')
}

radioAttach.prototype.attach = function(id, e){
    return(
    '<div class="card-image">' +
        '<img class="img-'+ id +' materialboxed" src="' + header.url + "resources/examen/" + header.archivo + "/" +  e.src + '">' +
    '</div>'
    )
}

radioAttach.prototype.view = function(e, i){
    var labels = "";

    var attach = "";

    e.result.map(function(e){
        labels += radioAttach.prototype.label(i, e);
    })

    if(e.attachment.length == 1){
        e.attachment.map(function(e){
            attach += radioAttach.prototype.attach(i, e);
        })
    }

    return ('<div class="row">' +
        '<div class="col s12 m12">'+
        '<div class="card white" style="background-color: #8ea4b0 !important">'+
            '<div class="card-content dark-text">'+
                '<span class="card-title">' + e.q + '</span>'+            
            '</div>'+
            attach +
            '<div class="card-content">' +
                '<div id="respuestas">'+
                    '<form action="#">'+
                        labels+
                    '</form>'+
                '</div>'+
            '</div>' +
        '</div>'+
        '</div>'+
    '</div>')
}

radioAttach.prototype.render = function(e, i){
    return radioAttach.prototype.view(e, i);
}