'use strict';
function radio(){}

radio.prototype.label = function(id, e){
  return(
  '<p>'+
      '<label>'+
          '<input class="with-gap" name="group-' + id + '" type="radio" value="' + e.r + '" onclick="handleRadio(' + id + ', this);"/>'+
          '<span>' + e.r + '</span>'+
      '</label>'+
  '</p>')
}


radio.prototype.view = function(e, i){
  var labels = "";
  e.result.map(function(e){
      labels += radio.prototype.label(i, e);
  })

  return ('<div class="row">' +
    '<div class="col s12 m12">'+
      '<div class="card white" style="background-color: #8ea4b0 !important">'+
        '<div class="card-content dark-text">'+
          '<span class="card-title">' + e.q + '</span>'+
          '<div id="respuestas">'+
              '<form action="#">'+
                labels+
              '</form>'+
          '</div>'+
        '</div>'+
      '</div>'+
    '</div>'+
  '</div>')
}

radio.prototype.render = function(e, i){
  return radio.prototype.view(e, i);
}
