'use strict';
function resume(){}

resume.prototype.view = function(total, correctas, incorrectas, puntaje){

  return (
    '<div class="row">'+
      '<div class="col s12 m12">'+
        '<div class="card white" style="background-color: #8ea4b0 !important">'+
          '<div class="card-content dark-text">'+
            '<span class="card-title">Tus resultados</span>'+
            '<div class="chip">'+
              '<i class="material-icons tiny">navigate_next</i>'+
              'Total '+ total + 
            '</div>'+
            '<div class="chip">'+
              '<i class="material-icons tiny">check</i>'+
              'Correctas '+ correctas + 
            '</div>'+
            '<div class="chip">'+
              '<i class="material-icons tiny">close</i>'+
              'Incorrectas '+ incorrectas+
            '</div>'+
            '<div class="chip">'+
              '<i class="material-icons tiny">playlist_add_check</i>'+
              'Puntaje '+ puntaje+
            '</div>'+
            '<canvas id="chart-graph" width="200" height="200"></canvas>'+
          '</div>'+
        '</div>'+
      '</div>'+
    '</div>')
}

resume.prototype.loading = function(){
    return (
        '<div class="row">' +
          '<div class="col s12 m12">'+
            '<div class="card white" style="background-color: #8ea4b0 !important">'+
              '<div class="card-content dark-text">'+
                '<span class="card-title">Tus resultados</span>'+
                '<center>'+               
                    '<div class="preloader-wrapper big active">'+
                    '<div class="spinner-layer spinner-blue-only">'+
                        '<div class="circle-clipper left">'+
                        '<div class="circle"></div>'+
                        '</div><div class="gap-patch">'+
                        '<div class="circle"></div>'+
                        '</div><div class="circle-clipper right">'+
                        '<div class="circle"></div>'+
                        '</div>'+
                    '</div>'+
                    '</div>'+
                '</center>'+
              '</div>'+
            '</div>'+
          '</div>'+
       '</div>'
    )
}

resume.prototype.render = function(total, correctas, incorrectas, puntaje){
  return resume.prototype.view(total, correctas, incorrectas, puntaje);
}